): Promise<CreateInvoiceResult> { // verify the invoice provided by the user const verification = await this.signer.verify(this.chainTip.linkId, remoteSignature); // return failure if signature fails if (!verification.valid) { return { success: false, error: "Invalid signature" }; } // Exercise: create the preimage // Exercise: create the memo // try to create the invoice try { const paymentRequest = await this.invoiceDataMapper.add(sats, memo, preimage); return { success: true, paymentRequest, }; } catch (ex) { return { success: false, error: ex.message, }; }
} When you are finished you can verify you successfully implemented the method with the following command: npm run test:server -- --grep AppController.*createInvoice
